INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35155f9cc0151562028670001', '8a8195b35112ed70015112f3280d0002', '1', '<p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体;">&nbsp;</span><span style="font-size: 14px; font-family: 宋体;">赴台学生保险专案保险条款</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">境外旅行意外伤害保险（2013版）</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">总则</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本保险合同由保险条款、投保单、保险单或其它保险凭证、批单组成。凡涉及本保险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 被保险人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人应为年满一周岁(含)(见释义1)至七十五周岁(含)、身体健康、能正常工作或正常生活，参加旅游团体或自行旅行活动的自然人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 投保人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">具有完全民事行为能力的被保险人本人、对被保险人有保险利益的其他人可作为投保人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人为限制民事行为能力人的，应由其监护人作为投保人。被保险人不满10周岁的，应由其父母作为投保人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 受益人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">订立本保险合同时，被保险人或投保人可指定一人或数人为身故保险金受益人。身故保险金受益人为数人时，应确定其受益顺序和受益份额；未确定受益份额的，各身故保险金受益人按照相等份额享有受益权。本保险合同的伤残保险金的受益人为被保险人本人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人或投保人可以变更身故保险金受益人，但需书面通知保险人(见释义2)，由保险人在本保险合同上批注。对因身故保险金受益人变更发生的法律纠纷，保险人不承担任何责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人指定或变更身故保险金受益人的，应经被保险人书面同意。被保险人为无民事行为能力人或限制民事行为能力人的，应由其监护人指定或变更身故保险金受益人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">受益人故意造成被保险人身故、伤残的，或者故意杀害被保险人未遂的，该受益人丧失受益权。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保障内容</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内被保险人在境外（见释义3)旅行期间遭受意外伤害(见释义4)，并因该意外伤害导致身故、伤残的，保险人依照下列约定给付保险金，且给付各项保险金之和不超过该被保险人的保险金额。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">境外旅行期间的开始时间以下列情况中最迟发生的时间为准:</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单所载的保险期间起始日；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人在保险期间内离开其境内(见释义5)日常居住地直接前往境外旅行目的地。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">该保险责任的终止时间以下列情况中最先发生的时间为准:</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单所载保险期间届满；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人完成境外旅行后，直接返回其日常居住地。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 身故保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内被保险人在境外旅行期间遭受意外伤害，并自意外伤害发生之日起180日内因该意外伤害身故的，保险人按保险金额给付身故保险金，对该被保险人的保险责任终止。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人因旅行遭受意外伤害且自该意外伤害发生日起下落不明，后经人民法院宣告死亡的，保险人按保险金额给付身故保险金。但若被保险人被宣告死亡后生还的，保险金领受人应于知道或应当知道被保险人生还后30日内退还保险人给付的身故保险金。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人身故前保险人已按本条款第七条约定给付伤残保险金的，身故保险金应予以扣除。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第七条 伤残保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内被保险人在境外旅行期间遭受意外伤害，造成本合同所附《人身保险伤残评定标准》所列伤残项目，我们依照该标准规定的评定原则对伤残项目进行评定，并按评定结果所对应该标准规定的给付比例乘以基本保险金额给付“意外伤残保险金”。如自意外伤害发生之日起180日内治疗仍未结束的，则按该意外伤害发生之日起第180日的身体情况进行伤残评定，并据此给付伤残保险金。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">该次意外伤害导致的伤残合并前次伤残可领较严重等级伤残保险金者，按较严重等级标准给付，但前次已给付的伤残保险金（投保前已患或因责任免除事项所致附件所列的伤残视为已给付伤残保险金）应予以扣除。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">意外伤残保险金以保险金额为限，累计给付的意外伤残保险金的总额达到保险金额时，本合同终止。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第八条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人因下列原因而导致身故、伤残的，保险人不承担给付保险金责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 投保人的故意行为；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人故意自伤或自杀，但被保险人自杀时为无民事行为能力人的除外；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 因被保险人挑衅或故意行为而导致的打斗、被袭击或被谋杀；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人犯罪或抗拒依法采取的刑事强制措施；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人因疾病、妊娠、流产、分娩、药物过敏、整容、整形手术或其它医疗导致的意外伤害；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人未遵医嘱服用、涂用、注射药物；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 被保险人受酒精、毒品、管制药物的影响；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">八) 被保险人因精神疾病发作导致的伤害；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">九) 疾病，包括但不限于高原反应、中暑、猝死(见释义6)；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十) 非因意外伤害导致的细菌或病毒感染；</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;&nbsp;&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十一) 任何生物、化学、原子能武器，原子能或核能装置所造成的爆炸、灼伤、污染或辐射；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十二) 恐怖袭击。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第九条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间遭受意外伤害导致身故、伤残的，保险人不承担给付保险金责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 战争、军事行动、武装叛乱或暴乱期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人从事违法、犯罪活动期间或被依法拘留、服刑、在逃期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 被保险人酒后驾车、无有效驾驶证(见释义7)驾驶或驾驶无有效行驶证(见释义8)的机动交通工具期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人从事潜水(见释义9)、跳伞、热气球运动(见释义10)、攀岩运动(见释义11)、探险活动(见释义12)、武术比赛(见释义13)、摔跤比赛、特技(见释义14)表演、赛马、赛车等高风险的活动期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人置身于任何飞机或空中运输工具(以乘客身份搭乘民用或商业航班者除外)期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人患有艾滋病(AIDS)或感染艾滋病病毒(HIV)(见释义15)期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 被保险人存在精神和行为障碍(以世界卫生组织颁布的《疾病和有关健康问题的国际统计分类(ICD-10)》为准)期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">八) 被保险人以医疗为目的或违背医嘱进行旅行期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">九) 被保险人参与执行军警任务或以执法者身份执行任务期间；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十) 被保险人参与任何职业体育活动或任何设有奖金或报酬的体育活动期间；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十一) 被保险人从事石油挖掘、采矿、空中摄影、处理爆炸物、森林砍伐、建筑工程、水上作业、高空作业之类的职业活动期间。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十条 保险金额及保险费</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金额是保险人承担给付被保险人保险金责任的最高限额。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金额由投保人、保险人双方约定，并在保险单中载明。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金额和每次意外伤害限额一经确定，未经保险人和投保人协商并一致同意后，在保险期间内不得变更。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险费依据保险金额与保险费率计收，并于保险合同上载明，投保人应按约定缴纳保险费。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十一条 保险期间</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本保险合同的保险期间以保险单上载明的起讫时间为准。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十二条 延期</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">如被保险人在保险期间内因不可抗力导致其旅程延长，至保险期间届满时仍未结束，保险人将按合理情况及需要，免费自动延长本保险合同的保险期间至该被保险人旅程结束。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人、被保险人义务</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十三条 交费义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">除另有约定外，投保人应当在保险合同成立时交清保险费。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十四条 年龄申报义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人申请投保时，应按被保险人的周岁年龄填写。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人申报的被保险人年龄不真实，并且其真实年龄不符合本保险合同约定的年龄限制的，保险人有权解除保险合同，并向投保人退还保险单的未满期净保险费(见释义16)。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十五条 如实告知义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人应如实填写投保单并回答保险人提出的询问，履行如实告知义务。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人故意或者因重大过失未履行前款规定的如实告知义务，足以影响保险人决定是否同意承保或者提高保险费率的，保险人有权解除合同。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人故意不履行如实告知义务的，保险人对于合同解除前发生的保险事故，不承担给付保险金的责任，并不退还保险费。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人因重大过失未履行如实告知义务，对保险事故的发生有严重影响的，保险人对于合同解除前发生的保险事故，不承担给付保险金的责任，但应当退还保险费。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十六条 适合旅行及其它条件</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人进行旅行时，必须身体状况良好适合旅行，或对不能进行正常旅行的情况并不知晓，否则保险人不承担给付保险金的责任。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十七条 住所或通讯地址的变更</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人的住所或通讯地址有变更时，应及时以书面通知保险人。投保人未通知的，保险人按本保险合同所载的最后住所或通讯地址发送的有关通知，均视为已发送投保人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十八条 其它内容变更通知义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，投保人需变更合同其它内容的，应以书面形式向保险人提出申请。保险人同意后出具批单，并在本保险合同中批注。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若被保险人已身故，则保险人不接受本保险合同中有关该被保险人的任何内容的变更申请。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第十九条 保险事故通知义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">发生保险责任范围内的事故后，投保人、被保险人或者保险金受益人应及时通知保险人，并书面说明事故发生的原因、经过和受伤害程度；故意或重大过失未及时通知保险人致使保险事故的性质、原因、损失程度难以确定的，保险人对无法确定的部分，不承担给付保险金责任，但保险人通过其它途径已经及时知道或者应当及时知道保险事故发生的除外。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">上述约定，不包括因不可抗力(见释义17)导致的迟延。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请与给付</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人(见释义18)向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十一条 身故保险金申请</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险金索赔申请书；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 保险金申请人的身份证明或户籍证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 公安部门或司法部门、保险人认可的医疗机构出具的被保险人死亡证明或验尸报告。若被保险人为宣告死亡，保险金申请人应提供法院出具的宣告死亡证明文件；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人的户籍注销证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 保险金申请人所能提供的与确认保险事故的性质、原因、伤害程度等有关的其它证明和资料。</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十二条 伤残保险金申请</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险金索赔申请书；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 被保险人身份证明或户籍证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 司法部门、保险人认可的医疗机构、保险人认可的其它鉴定机构出具的伤残鉴定诊断书；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 保险金申请人所能提供的与确认保险事故的性质、原因、伤害程度等有关的其它证明和资料。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十三条 诉讼时效期间</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人请求给付保险金的诉讼时效期间为2年，自其知道或者应当知道保险事故发生之日起计算。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十四条 保险金的给付</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人在收到保险金申请人提交的本保险条款所列的材料后，应及时就是否属于保险责任做出核定，情形复杂的，保险人在收到保险金申请人的上述请求后30日内未能核定保险责任的，保险人与保险金申请人根据实际情形商议合理期间，保险人在商定的期间内做出核定结果并通知保险金申请人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人应当将核定结果通知被保险人或者受益人，对属于保险责任的，保险人应在与保险金申请人达成有关给付保险金数额的协议后10日内，履行给付保险金义务；对不属于保险责任的，应当自做出核定之日起3日内向保险金申请人发出拒绝给付保险金通知书，并说明理由。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人自收到给付保险金的请求和有关证明、资料之日起60日内，对其给付保险金的数额不能确定的，应当根据已有证明和资料可以确定的数额先予支付；保险人最终确定给付保险金的数额后，应当支付相应的差额。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十五条 外国货币的计价</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">对被保险人的损失或保险人理赔金额的计算涉及外国货币时，其汇率计算以下列期日之中国人民银行即期现金卖出汇价为准：</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 以国外所开立的收据申请理赔的，以收据开立日为汇率计算日；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 由保险人直接垫付的，以保险人垫付日为汇率计算日。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">合同的解除和争议处理</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十六条 合同的解除</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在本保险合同成立后，投保人可以书面形式通知保险人解除合同，但保险人已根据本保险合同约定给付保险金的除外。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人解除本保险合同时，应填写保险合同解除申请书并提供下列证明文件和资料：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险合同解除申请书；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 保险单正本；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 保险费交付凭证；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 投保人身份证明。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">投保人要求解除本保险合同，可于保险期间内至少提前三十天以书面形式向保险人申请退保，本保险合同将于书面通知上载明的合同终止日二十四时终止。保险人收到上述证明文件和资料之日起30日内退还保险单的现金价值。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人取得的保险合同解除权，自保险人知道有解除事由之日起超过30日不行使而消灭。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人在合同订立时已经知道投保人未如实告知的情况的，保险人不得解除合同；发生保险事故的，保险人应当承担赔偿或者给付保险金的责任。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二十七条 合同的争议处理</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因履行本保险合同发生的争议，由当事人协商解决。协商不成的，提交保险单载明的仲裁机构仲裁；保险单未载明仲裁机构且争议发生后未达成仲裁协议的，依法向有管辖权的人民法院起诉。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">与本保险合同有关的以及履行本保险合同产生的一切争议处理适用中华人民共和国法律(不包括港澳台地区法律)。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">周岁</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指以法定身份证明文件中记载的出生日期为基础计算的实足年龄。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指与投保人签订本保险合同的富邦财产保险有限公司及各级分支机构。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">境外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指中国大陆以外的国家和地区，该地区包括台湾、香港、澳门地区。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">4.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">意外伤害</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指以外来的、突发的、非本意的、非疾病的客观事件为直接且单独的原因致使身体受到的伤害。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">5.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">境内</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指中国大陆地区范围内，该地区不包括台湾、香港、澳门地区。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">6.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">猝死</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指外表看似健康的人由于潜在的疾病或者功能障碍所引起的突然的意外的死亡。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">7.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">无有效驾驶证</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人存在下列情形之一者：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 无驾驶证或驾驶证有效期已届满；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 驾驶的机动车与驾驶证载明的准驾车型不符；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 实习期内驾驶公共汽车、营运客车或者载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车，实习期内驾驶的机动车牵引挂车；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 持未按当地政府机关交通管理部门规定审验的驾驶证，以及在暂扣、扣留、吊销、注销驾驶证期间驾驶机动车；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 使用各种专用机械车、特种车的人员无当地政府机关有关部门核发的有效操作证，驾驶营业性客车的驾驶人无当地政府机关有关部门核发的有效资格证书；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 依照当地法律法规或当地政府机关交通管理部门有关规定不允许驾驶机动车的情况下驾车。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">8.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">无有效行驶证</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指下列情形之一：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 机动车被依法注销登记的；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 无当地政府机关交通管理部门核发的行驶证、号牌，或临时号牌或临时移动证的机动交通工具；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 未在规定检验期限内进行机动车安全技术检验或检验未通过的机动交通工具。未依法按时进行或通过机动车安全技术检验。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">9.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">潜水</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指以辅助呼吸器材在江、河、湖、海、水库、运河等水域进行的水下运动。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">10.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">热气球运动</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指乘热气球升空飞行的体育活动。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">11.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">攀岩运动</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指以攀登悬崖、楼宇外墙、人造悬崖、冰崖、冰山等运动。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">12.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">探险活动</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指明知在某种特定的自然条件下有失去生命或使身体受到伤害的危险，而故意使自己置身其中的行为。如江河漂流、徒步穿越沙漠或人迹罕至的原始森林等活动。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">13.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">武术比赛</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指两人或两人以上对抗性柔道、空手道、跆拳道、散打、拳击等各种拳术及各种使用器械的对抗性比赛。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">14.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">特技</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指从事马术、杂技、驯兽等特殊技能。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">15.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">艾滋病(AIDS)或艾滋病病毒(HIV)</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">艾滋病病毒是指人类免疫缺陷病毒，英文缩写为HIV。艾滋病是指人类免疫缺陷病毒引起的获得性免疫缺陷综合症，英文缩写为AIDS。在人体血液或其它样本中检测到艾滋病病毒或其抗体呈阳性，没有出现临床症状或体征的，为感染艾滋病病毒；如果同时出现了明显临床症状或体征的，为患艾滋病。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">16.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">未满期净保险费</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">未满期净保险费=保险费×[1-(保险单已经过天数/保险期间天数)]×(1-25%)。经过天数不足一天的按一天计算。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">17.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">不可抗力</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指不能预见、不能避免并不能克服的客观情况。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">18.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">身故保险金申请人是指受益人或被保险人的继承人或依法享有保险金请求权的其它自然人；伤残保险金申请人是指被保险人本人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行急性病身故保险</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，被保险人在境外旅行期间突发急性病（见释义1），并在该疾病发生后30日内因该急性病直接导致身故的，保险人按本附加险合同项下的保险金额给付身故保险金，对该被保险人的保险责任终止。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因造成被保险人突发急性病身故的，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 既往病症（见释义2）及其并发症、慢性病、精神病、性传播疾病、感染艾滋病病毒或患艾滋病（见释义3）、遗传性疾病、先天性疾病或缺陷、先天性畸形、变异、染色体异常。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 在旅程开始前可以预见的受保前已存在疾病的恶化。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 药物过敏或其他医疗行为导致的伤害。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 由于服用酒精饮料、毒品、麻醉剂、镇静剂、安眠药或其他麻醉性物品所导致的精神疾病或意识不清所引发的疾病。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">下列情形下被保险人突发急性病身故的，保险人不承担给付保险金是责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人开始旅程的时候已经知道如果旅程按计划进行其必须出于医学原因接受由医生要求的医学治疗或其他治疗（如透析）。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 保险金申请人的户籍证明或者身份证明；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 医疗机构出具的疾病诊断证明书；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人死亡的，应提供相关部门出具的被保险人死亡证明书，以及被保险人的户籍注销证明；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 保险金申请人所能提供的与确认保险事故的性质、原因、损失程度等有关的其他证明和资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">突发急性病</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人在保险生效之前未曾接受治疗或诊断、在旅行期间突然发生的、并且必须立即接受治疗方能避免身体或生命伤害的疾病，不包括既往疾病、慢性病、精神病、精神分裂、艾滋病、性传播疾病、遗传性疾病、先天性疾病或缺陷、先天性畸形、牙齿治疗（但因意外伤害事故导致的必须进行的牙科门诊治疗不在此限）、预防性手术等非必须紧急治疗的手术、器官移植。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">既往病症</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人出发旅行前一百八十天内身体上已经出现或存在、可以或已经引致一正常而审慎的人寻求诊断、医疗护理或医药治疗的任何疾病、症状和体征。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">感染艾滋病病毒或患艾滋病</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">艾滋病病毒指人类免疫缺陷病毒，英文缩写为HIV。艾滋病指人类免疫缺陷病毒引起的获得性免疫缺陷综合征，英文缩写为AIDS。在人体血液或其他样本中检测到艾滋病病毒或其抗体呈阳性，没有出现临床症状或体征的，为感染艾滋病病毒；如果同时出现了明显临床症状或体征的，为患艾滋病。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行医疗保险</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 受益人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同保险金的受益人为被保险人本人。</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 在保险期间内，如被保险人在境外旅行期间遭受意外伤害或罹患疾病，并自该意外伤害发生之日或罹患疾病之日起90天内在医疗机构（见释义1）进行治疗，对于在上述90天内发生的必需且合理的医疗费用（见释义2），保险人在扣除免赔额后，按照赔付比例给付医疗费用补偿保险金。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人对被保险人累计给付的医疗费用补偿保险金以被保险人的保险金额为限。被保险人的保险金额、免赔额和赔付比例以保险单上所载本附加险合同项下的相应数额为准。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 如被保险人因意外伤害或疾病于境外就医，并于返回其境内日常居住地后30天内因该意外伤害或该疾病需继续住院（见释义3）接受后续治疗的，保险人对上述30天内因住院治疗发生的必需且合理的医疗费用亦予以给付，该后续治疗费用的补偿以保险金额的10%为限。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">上述两项保险金的给付总额以保险单上所载的本附加险合同项下的保险金额为限。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同适用补偿原则。被保险人通过任何途径所获得的医疗费用补偿金额总和以其实际支出的医疗费用金额为限。被保险人已经从社会基本医疗保险或任何第三方(包括任何商业医疗保险)获得相关医疗费用补偿的，保险人仅对扣除已获得补偿后的剩余医疗费用，按照合同约定承担给付保险金责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因造成的医疗费用，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 既往病症（见释义4）及其并发症；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 先天性疾病与先天性畸形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 非因意外伤害而进行的牙科治疗或手术以及任何原因导致的牙齿修复或牙齿整形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 非因意外伤害而进行的视力矫正或因矫正视力而作的眼科验光检查；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 一般身体检查、疗养、特别护理或静养、康复性治疗、物理治疗或心理治疗；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 椎间盘突出症（包括椎间盘膨出、椎间盘突出、椎间盘脱出、游离型椎间盘等类型）；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">八) 根据被保险人的主治医生（见释义5）的意见，可以被合理延迟至被保险人返回原出发地后进行而被保险人坚持在境外进行的治疗或手术。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间遭受意外伤害或罹患疾病而发生的医疗费用，保险人不承担给付保险金责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人身份证明；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 完整的门、急诊病历；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 出院小结；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 医疗费用收据正本及明细清单/帐单；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 保险金申请人所能提供的其它与确认保险事故的性质、原因、损失程度等有关的其它证明及资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医疗机构</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本保险合同约定的医院或医疗机构是指符合下列所有条件的机构：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">拥有合法经营执照；</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;&nbsp;&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">设立的主要目的为向受伤者和患病者提供留院治疗和护理服务；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">有合格的医生和护士提供全日二十四小时的医疗和护理服务；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(4)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">非主要作为康复医院、诊所、护理、疗养、戒酒、戒毒或类似的医疗机构。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若医疗机构处于中国境内（不包括港、澳、台地区），则医院必须是中华人民共和国卫生部医院等级分类中的二级或二级以上的医院或保险人认可的医疗机构。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">必需且合理的实际医疗费用</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指由医生或医院根据被保险人病伤或伤情，实施必要的医疗行为所发生的医疗费用，中国境内（不包括港、澳、台地区）发生的医疗费用应是符合本保险合同签发地政府颁布的基本医疗保险报销范围。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">住院</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人确因临床需要，正式办理入院及出院手续，并确实入住医疗机构正式病房接受治疗的行为过程，且入住医疗机构必须达二十四小时以上且由医疗机构收取病房或床位费用。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">4.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">既往病症</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人出发旅行前一百八十天内身体上已经出现或存在、可以或已经引致一正常而审慎的人寻求诊断、医疗护理或医药治疗的任何疾病、症状和体征。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">5.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医生</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指在医疗机构内行医并拥有处方权的医生，亦指在被保险人接受诊断、医疗、处方或手术的地区内合法注册且有行医资格的医生，医生不能为被保险人本人、被保险人的代理人、合伙人、雇员或雇主，或被保险人的家庭成员，如配偶、兄弟、姐妹、父母、子女以及其他具有类似关系的人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;&nbsp;&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行住院关怀慰问金保险</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 受益人</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同保险金的受益人为被保险人本人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，保险人对于被保险人因在境外（含港澳台）旅行时遭受意外伤害，并于当地合法医疗机构(见释义1)住院(见释义2)达三日(含)以上时，每次事故给付保险单上所载住院关怀慰问金，但保险期间内最高以给付一次为限。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因造成遭受意外伤害的，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 根据被保险人的主治医生的意见，可以被合理延迟至被保险人返回境内后进行而被保险人坚持在境外进行的治疗或手术。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间遭受意外伤害的，保险人不承担给付保险金的责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人身份证明；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 医师诊断证明书或住院证明；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 保险金申请人所能提供的其它与确认保险事故的性质、原因、损失程度等有关的其它证明及资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医疗机构</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指本保险合同约定的医院或医疗机构是指符合下列所有条件的机构：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">拥有合法经营执照；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">设立的主要目的为向受伤者和患病者提供留院治疗和护理服务；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">有合格的医生和护士提供全日二十四小时的医疗和护理服务；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(4)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">非主要作为康复医院、诊所、护理、疗养、戒酒、戒毒或类似的医疗机构。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若医疗机构处于中国境内（不包括港、澳、台地区），则医院必须是中华人民共和国卫生部医院等级分类中的二级或二级以上的医院或保险人认可的医疗机构。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">住院</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人确因临床需要，正式办理入院及出院手续，并确实入住医疗机构正式病房接受治疗的行为过程，且入住医疗机构必须达二十四小时以上且由医疗机构收取病房或床位费用。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行紧急救援保险</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，被保险人持有效证件在境外旅行期间（见释义1）因遭受意外事故或者罹患急性病（见释义2）时，经保险人委托的境外救援机构（以下简称救援机构）确认被保险人需要以下救助服务时，在该被保险人紧急救援保险金额范围内，救援机构作为保险人的受托人承担以下责任及其费用：</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 医疗协助</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">电话医疗咨询</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">提供24小时电话医疗资讯和医疗建议。但此服务仅属于咨询性质，不构成病情诊断。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">推荐医疗服务机构</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">应被保险人要求提供境外医师、医院、门诊部、牙医以及牙科门诊部（合称“医疗服务机构”）的名字、地址、电话号码等信息，并尽可能提供其营业时间。救援机构就其所推荐医疗服务机构，除应尽善良管理人注意义务筛选外，不为任何服务质量做保证，被保险人应自行决定所需医疗服务机构。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排预约当地医生看诊</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">协助被保险人代为预约当地医师看诊。但所有相关之费用须由被保险人自行负担。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">4.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排住院</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">如被保险人病情严重需住院治疗，救援机构将协助安排住院。但所有相关之费用须由被保险人自行负担。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">5.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医疗转送或转送回国前的病况观察</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在执行医疗转送或转送回国前，救援机构将于被保险人住院期间对其医疗情况进行追踪监控以确保被保险人适于医疗转送或转送回国，但涉及被保险人个人隐私的事项，应经被保险人适当授权。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">6.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医疗传译服务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">为被保险人安排透过电话进行医疗传译服务。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">7.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">递送必要医疗器材与药物</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">救援机构为被保险人安排递送其护理和治疗所必需的，而在被保险人所在地无法获得的医疗器材与药物。但被保险人应当负责支付药物和医疗器械的费用以及任何递送费用。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">8.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">医疗运送费用</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在中国境外旅行过程中因突发疾病或遭受意外伤害事故，需要紧急救治，保险人委托救援机构履行以下紧急医疗运送：</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人突发疾病（但不包括只需通过门诊就可以诊疗的轻微疾病）或遭受意外伤害事故，须到医疗机构紧急救治的，将被保险人从事故地运送到初诊医疗机构；</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若救援机构的授权医生认为被保险人病情须转院治疗的，援救机构将被保险人从初诊医疗机构转送到医疗条件适合的医院治疗；</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若授权医生认为被保险人病情需要，救援机构将派遣医护人员护送被保险人转院；</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(4)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">对被保险人的紧急医疗运送手段，以在事故发生地能够提供的最合适的手段为限。若以空运为转运方式，救援机构将使用正常航班（经济舱位）。若救援机构的授权医生认为必要，救援机构将雇用包机或者使用医疗救护专用飞机运送被保险人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">9.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">紧急医疗转运回国费用</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在被保险人的救护措施结束后，或者当救援机构的授权医生认为被保险人的病情可以旅行时，保险人委托的救援机构将安排被保险人乘坐正常航班(经济舱位)返回中国境内。若救援机构的授权医生认为被保险人病情需要医疗护送，救援机构将在转运被保险人回中国境内过程中安排医疗护送。</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若救援机构的授权医生认为被保险人情况允许，救援机构将安排其回到中国境内离其居住地最近的国际机场，该次转运回国的保险责任终止。</span></p><p style="margin-bottom: 2px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若救援机构的授权医生认为被保险人在抵达中国境内时需入院治疗，被保险人将被送到中国境内离其居住地最近的国际机场所在的城市，被保险人指定的任意一家医院；若被保险人未指定或者不能指定有关医院，被保险人将被送至救援机构指定的医院，该次转运回国的保险责任终止。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(4)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若救援机构的授权医生认为被保险人的健康状况允许其乘坐正常航班（经济舱位）返回中国境内，救援机构有权尽可能使用被保险人开始旅行时购买的原始回程机票，如发生更改日期或升舱费用，由救援机构支付。若被保险人无原始回程机票，则被保险人从所在国返回中国境内的单程机票费由被保险人自行承担。若被保险人所购买的原始回程机票，由于救助过程而过期失效，救援机构将支付被保险人的回程机票费，但回收被保险人之原始机票。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人承担医疗运送及转运回国保险责任以本保险合同项下载明的保险金额为限，救援机构在该限额内履行职责，一次或累计履行医疗运送及转运回国保险责任所支付的费用达到本保险合同项下载明的保险金额时，本合同的该项保险责任终止。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">10.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">遗体或骨灰运送回国或就地安葬费用</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在中国境外旅行过程中因突发疾病或遭受意外伤害事故导致身故，保险人委托救援机构按照被保险人的遗愿或者其家属的愿望履行下列保险责任。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">遗体转运回中国境内。按照被保险人的遗愿或其家属的愿望以及当地政府的规定，负责用正常航班将被保险人的遗体从事发地运至中国境内离其居住地最近的国际机场，并支付灵柩费及运送费。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">当地火葬及遗骨转送回国。若被保险人遗愿或者其家属选择火葬，救援机构将支付其遗体在事故发生国家的火葬费，并将骨灰运回中国境内的运送费用（以正常航班的费用为标准）。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">就地安葬。若被保险人遗愿或者其家属选择就地安葬，救援机构将支付就地安葬的费用。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">上列保险责任以外的费用，例如：告别礼厅、宗教仪式，或非必要手续费用等，保险人不承担保险责任，救援机构将不承担费用支付责任。保险人承担遗体或遗骨转运回国或就地安葬保险责任以本保险合同载明的保险金额为限，救援机构在该限额内履行，履行遗体或遗骨转运回国或就地安葬保险责任所支付的费用达到本保险合同载明的保险金额时，本保险合同的该项保险责任终止。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">11.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排亲友探视及住宿</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人独自在境外住院治疗超过7天时，救援机构将视病情需要，负责提供一张单程机票及住宿基本费用，让被保险人的一名亲属或朋友前往探视。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">12.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">亲属处理后事费用</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在中国境外旅行过程中因突发疾病或遭受意外伤害事故导致身故，为方便安排后事，救援机构安排被保险人亲友前往处理后事，负责提供机票订位及住宿安排。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">有关后事需由被保险人直系亲属前往处理的，保险人将支付一位被保险人直系亲属的交通和住宿费用（以正常航班的经济舱位费用为标准）。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人承担亲属处理后事保险责任以本保险合同载明的保险金额为限，履行亲属处理后事保险责任所支付的费用达到本保险合同载明的保险金额时，本保险合同的该项保险责任终止。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">13.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">代转住院医疗费用及保证金</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因意外伤害或突发疾病住院而需救援机构提供代转住院医疗费用及保证金服务时，救援机构协助代转被保险人于住院期间（含住宿及医师费）所发生的医疗费用，最多以美金5，000元为限。代转服务必须是被保险人家属或其它亲友缴足款项，方才提供。若被保险人赴欧洲申根国家旅游时，本项服务不适用，改为提供第(13)项代垫住院医疗费用及保证金服务。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">14.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">代垫住院医疗费用及保证金（旅游地区为欧洲申根国家适用）</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若被保险人赴欧洲申根国家旅游时，因意外伤害或突发疾病住院而需要救援机构提供代垫住院医疗费用及保证金服务时，经富邦财产保险授权且取得「住院医疗费用代垫担保偿还证明书」后，救援机构可协助代垫被保险人于住院期间（含住宿及医师费）所发生的医疗费用，以欧元30，000元为限。代垫，必须是救援机构先取得被保险人家属或其它亲友提供的「住院医疗费用代垫担保偿还证明书」及理赔申请书，方才提供服务。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">15.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">协助未成年（16周岁）家属回国费用</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在中国境外旅行过程中因突发疾病或遭受意外伤害事故导致住院或身故，并造成其随行的未满十六周岁儿童无人照顾时，救援机构将安排该儿童返回中国境内离其居住地最近的国际机场，并尽量使用其开始旅行时购买的原始回程机票。如有必要，可安排人员陪送。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">若被保险人所购买的原始回程机票，由于救助过程而过期失效，保险人将支付该儿童的回程正常航班（经济舱位）的费用，但回收被保险人之原始机票。如救援机构为该儿童安排一位随行人员，保险人支付该随行人员的交通费用。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人承担协助未满十六周岁儿童回国保险责任以本保险合同载明的保险金额为限，救援机构在该限额内履行，一次或累计履行协助未满十六周岁儿童回国保险责任所支付的费用达到本保险合同载明的保险金额时，本保险合同的该项保险责任终止。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">16.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排配偶回国</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人由于意外事故、急性病被实施紧急医疗转运，导致其同行的配偶无人照顾的，救援机构负责将该家属送回中国境内离其居住地最近的国际机场，并尽量使用其开始旅行时购买的原始回程机票。如有必要，可安排人员陪送。但所有相关之费用须由被保险人亲属自行负担。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">17.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">出院后疗养</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因遭遇急难事故住院，经当地主治医师及救援机构医师共同认定其于出院后须就近疗养者，救援机构可代为安排住宿事宜，但所有相关费用由被保险人自行承担。</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 旅游协助</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">提供接种和签证要求的信息。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">提供前往他国所需签证及接种要求的相关信息，并告知引述来源文件名称，但对引述内容的正确性不做任何形式的保证。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">通译服务的推荐及秘书介绍</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">向被保险人提供外国通译或秘书的名称、住址、电话号码，并根据被保险人要求尽可能提供服务时间。救援机构就其所推荐通译或秘书，除应尽善良管理人注意义务筛选外，不为任何服务质量做保证，被保险人应自行决定所需通译服务机构。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">遗失行李找寻</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人境外旅行遗失行李时，救援机构协助向有关机构办理相关手续。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">4.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">遗失护照之协助</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行遗失护照时，救援机构应协助其向有关机关办理相关手续。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">5.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">重要旅游文件的补发与递送</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行遗失重要旅游文件时，救援机构提供相关信息以协助被保险人申请补发及递送。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">6.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">行前信息</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">提供被保险人国外签证、国定假日、汇率、语文、天气、运输/班机信息。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">7.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">紧急传译服务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行而有紧急需要时，救援机构将安排透过电话向其提供传译服务。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">8.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">使领馆信息</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">救援机构应向被保险人提供某国设于他国之最近使领馆住址、电话号码以及办公时间等有关信息。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">9.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">紧急信息或文件传送</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">救援机构应于被保险人提出要求时，协助其安排将紧急讯息或文件传送予其亲友或公司。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">10.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排签证延期</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行，因住院治疗导致签证过期，救援机构将协助其办理签证延期。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">11.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">代订机位</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行，因紧急状况需要代订机位时，救援机构将协助其办理订位事宜。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">12.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">国外租车、旅馆安排服务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险在境外旅行，因紧急状况需要租车、旅馆安排服务时，救援机构将协助其办理。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">上述服务纯属提供推荐或协助安排，由此产生所有相关费用均由被保险人自行承保。</span></p><p style="margin-bottom: 1px; white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 法律协助</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">推荐法律服务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">救援机构应向被保险人提供律师与法律执业人员之姓名、住址、电话号码，经其要求并应尽可能提供其服务时间。救援机构并不负责法律服务之提供，就所推荐之法律服务，除应以善良管理人之注意义务为筛选外，不为任何服务质量之保证，被保险人应自行决定所需法律服务提供者。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">安排预约律师</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">救援机构将协助被保险人与律师预约，但相关费用由被保险人自行负担。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">代转保释金</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在境外旅行被要求支付保释金时，倘其以其信用卡或由其亲友向救援机构交付所需金额及处理费，救援机构将为其安排代转事宜。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">上述服务纯属提供推荐或协助安排，由此产生所有相关费用均由被保险人自行承保。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 补偿原则</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同适用补偿原则。被保险人通过任何途径所获得的医疗费用补偿金额总和以其实际支出的医疗费用金额为限。被保险人已经从社会基本医疗保险或任何第三方（包括任何商业医疗保险）获得相关医疗费用补偿的，保险人仅对扣除已获得补偿后的剩余医疗费用，按照合同约定承担给付保险金责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因造成被保险人遭受意外伤害或者患急性病的，保险人不承担紧急救援保险责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 在建筑工地、矿场、油田或者石油及化学工业现场等地进行职业活动；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 既往病症（见释义5）及其并发症；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 先天性疾病与先天性畸形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 性传播疾病、法定传染病。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间遭受意外伤害或者患急性病的，保险人也不承担紧急救援保险责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人无合法居留身份。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 地区除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在以下国家和地区发生的事故，保险人不承担紧急救援保险责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">亚洲：阿富汗、伊拉克、可可群岛、东帝汶、英属印度洋领地。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">非洲：厄立特里亚、卢旺达、索马里、西撒哈拉、圣赫勒拿岛。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">大洋洲：美属萨摩亚群岛、博维岛、圣诞岛、法属太平洋领地、赫德-迈克唐那群岛、基里巴斯、马歇尔群岛、麦克罗尼西亚、瑙鲁、尼乌亚岛、巴伯儿图阿普群岛、皮特肯群岛、所罗门群岛、南乔治亚和南桑威治、托客劳群岛、汤加、图瓦卢、美国本土外的小岛、瓦努阿图、沃利斯和富纳群岛。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">南极洲：南极洲。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 其他事项</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">未经救援机构授权医生事先同意的转运和救护，保险人也不承担紧急救援保险责任。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第七条 保险金额</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金额是保险人承担给付保险金责任的最高限额。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险的保险金额由投保人、保险人双方约定，并在保险单中载明。保险金额不得超过主险合同保险金额。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金额一经确定，在保险期间内不得变更。</span></p><p style="white-space: normal; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第八条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人身份证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 被保险人在境外就医的，需提供境外医院出具的医疗费用凭证、诊断证明、住院证明及病历等；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 保险金申请人所能提供的其它与确认保险事故的性质、原因、损失程度等有关的其它证明及资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">急性病</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指在本附加险合同保险期间内，被保险人在境外旅行期间发生以下的疾病或症状，且该疾病或症状不在本附加险第三条原因除外范围内：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(1)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">高热（成人达到38.5摄氏度或以上，小儿达到39摄氏度或以上）；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(2)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">急性阑尾炎或剧烈呕吐或严重腹泻；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(3)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">休克或昏迷；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(4)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">高原反应；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(5)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">癫痫发作；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(6)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">严重喘息或呼吸困难；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(7)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">急性心肌梗塞或心力衰竭或严重心律失常；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(8)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">高血压危象、高血压脑病、脑血管意外；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(9)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">非因意外伤害所导致的出血；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(10)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">急性尿潴留；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(11)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">食物中毒；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(12)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">非因意外伤害所导致的、突发性的眼睛红肿、疼痛或视力障碍。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(13)&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">其他不及时救治将危及生命安危的急性疾病，不包括原来已患有的慢性病的急性发作。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">住院</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人确因临床需要，正式办理入院及出院手续，并确实入住医疗机构正式病房接受治疗的行为过程，且入住医疗机构必须达二十四小时以上且由医疗机构收取病房或床位费用。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">3.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">既往病症</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人出发旅行前一百八十天内身体上已经出现或存在、可以或已经引致一正常而审慎的人寻求诊断、医疗护理或医药治疗的任何疾病、症状和体征。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行旅行证件丢失保险</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，若被保险人在境外旅行期间因被抢劫或被盗窃导致护照、旅行票据（见释义1）或其它旅行证件丢失，且在发现损失后二十四小时内向当地警方申报并取得书面警方报告，保险人赔偿被保险人因重置护照、旅行票据及其它旅行证件的费用，包括被保险人因被滞留于旅行当地，所支出的住宿、餐钦及交通费用，最高以保险单所载本附加险合同项下的保险金额为限。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同的每次保险事故的免赔额以保险单上载明的本附加险合同项下的免赔金额为准，保险人对小于免赔额的损失不承担赔偿责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因造成被保险人的损失，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 损失发生后的二十四小时内尚未向警方报告且无警方书面证明的损失；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 于旅行结束后已不必要的旅行证件或签证费用；或丢失的护照、旅行票据及其它旅行证件重置成功后的所有开支。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间造成旅行证件丢失，保险人不承担给付保险金的责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人应于盗窃或抢劫发生后二十四小时内向警方或其他有关当局报案，并取得书面证明文件。保险金申请人向保险人申请索赔保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人身份证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 警方的书面证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 重置护照、旅行票据、其它旅行证件及其他费用的发票或收据正本；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 保险金申请人所能提供的与确认保险事故的性质、原因、损失程度等有关的其他证明和资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">旅行票据</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指为此次旅行所购买，但还未使用且必须重新购买的飞机票、船票、车票或火车票。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行托运行李延误保险</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，被保险人在境外（含港澳台）旅行期间，在被保险人抵达所乘航班班机的预定目的地(不包含原出发地或居住地)后，其随行托运行李(见释义1)在八小时后仍未送抵，保险人赔偿被保险人于当地因紧急需要购买日用必需品、必要的费用，但以保险单上所载该项责任赔偿限赔为限。&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因直接或间接造成的托运行李延误，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外中各款之情形；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人的托运行李被海关或其它政府部门没收、扣留、隔离、检验或销毁；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 被保险人抵达预定目的地(不含原出发地或居住地)后未将行李延误一事通知有关航空公司或其代理人取得行李延误时数的书面证明；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 被保险人留置其行李于航空公司或其代理人。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人在下列期间造成其行李和随身物品丢失或损坏，保险人不承担给付保险金的责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 被保险人义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">被保险人应在发现托运行李延误后，立即向航空公司报告，并于发现延误后取得航空公司的书面证明。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人的身份证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 航空公司出具的延误时间及原因的书面证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 因行李延误后在当地购买日用必需品费用的凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 被保险人境外旅行的护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 保险金申请人所能提供的与确认保险事故的性质、原因、损失程度等有关的其它证明和资料。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">、 托运行李</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指被保险人搭乘班机时交由承运人负责照管和运输、并已经出具托运行李凭据的行李，不包括托运的用于商业活动的物品或样品。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">附加境外旅行个人责任保险</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第一条 合同构成</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本条款是主险合同的附加险条款。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">本附加险合同与主险合同相抵触之处，以本附加险合同为准。本附加险合同未约定事项，以主险合同为准。主险合同效力终止，本附加险合同效力亦同时终止；主险合同无效，本附加险合同亦无效。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡涉及本附加险合同的约定，均应采用书面形式。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第二条 保险责任</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在保险期间内，若被保险人在境外旅行期间因疏忽或过失行为造成第三方（见释义1）死亡、身体伤害或直接财产损失，对于依法应由被保险人承担的经济赔偿责任，第三方在保险期间内向被保险人提出索赔的，则保险人以保险单所载的本附加险合同项下的责任限额为限，补偿被保险人所实际支付的赔偿金额。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">责任免除</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三条 原因除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">因下列原因而承担的任何个人责任，保险人不承担给付保险金的责任：</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同原因除外各款之情形。</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人受酒精、毒品、管制药物的影响；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 由被保险人所拥有或者由被保险人监管或控制下的动物或财产损失；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 任何由于被保险人故意、恶意、违法、犯罪、不正当行为造成的损坏或伤害；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 贸易、商业或职业行为；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人所有、占有、使用、租借、保管下的任何土地、房屋建筑、财产的损坏。但被保险人租用的酒店房间或度假屋（不包括其中的家具和设备）的损坏不在此限。</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 使用或拥有海、陆、空运输工具（无论有无营运执照）；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">八) 被保险人参加赛马、赛车、使用枪支；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">九) 任何对被保险人的直系亲属（见释义2）雇主或雇员人身或财产造成的损坏或伤害；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十) 任何由法院裁判的惩罚性、警戒性责任或罚款、惩罚；</span></p><p style="margin-bottom: 2px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十一) 被保险人履行合同或协议约定赔偿的义务；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">十二) 被保险人传播疾病。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第四条 期间除外</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">在下列期间的任何个人责任，保险人不承担任何赔偿责任：</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 主险合同期间除外中各款之情形。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第五条 损害赔偿请求通知义务</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">未经保险人书面同意，被保险人自行对第三方作出的任何承诺、拒绝、出价、约定、付款或赔偿，保险人不承担赔偿责任。保险人有权自行或以相关被保险人名义处理有关诉讼或仲裁事宜，有权为维护自身利益向其他有关各方请求赔偿，被保险人应提供有关文件，并给予必要的协助。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第六条 保险金申请</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险金申请人向保险人申请给付保险金时，应填写保险金索赔申请书，并提交以下材料。保险金申请人因特殊原因不能提供以下材料的，应提供其它合法有效的材料。若保险金申请人委托他人申请的，还应提供授权委托书原件、委托人和受托人的身份证明等相关证明文件。保险人按照本保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知保险金申请人补充提供。保险金申请人未能提供有关材料，导致保险人无法核实该申请的真实性的，保险人对无法核实部分不承担给付保险金的责任。</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">凡由境外机构或人员出具的文件必须经境外出险地合法公证机构对文件的有效性及真实性进行公证，或经中国驻当地所在国使领馆认可。</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 保险单正本或其它保险凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 被保险人身份证明；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 判决书、裁决书或调解书（如有）；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 赔偿协议（如有）；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">五) 赔偿给付凭证；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">六) 被保险人境外旅行的证明，如护照、签证及机票或车船票；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">七) 保险金申请人所能提供的与确认保险事故的性质、原因、损失程度等有关的其他证明和资料。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第七条 赔偿责任确定基础</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">保险人的一确定的被赔偿以下列方式之保险人的赔偿责任为基础：</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">一) 被保险人与向其提出损害赔偿请求的第三方协商并经保险人确认；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">二) 仲裁机构裁决；</span></p><p style="margin-bottom: 1px; white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">三) 人民法院判决；</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">(</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">四) 保险人认可的其它方式。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第八条 事故损失赔偿金额计算</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">发生保险责任范围内的损失，保险人在责任限额内计算赔偿。</span></p><p style="white-space: normal; text-indent: 24px; page-break-before: always;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">释义</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">1.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">第三方</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">&nbsp;</span></p><p style="white-space: normal; text-indent: 24px;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">是指与被保险人没有抚养、扶养及赡养关系的人。</span></p><p style="white-space: normal;"><span style="font-size: 14px; font-family: 宋体; color: windowtext;">2.&nbsp;</span><span style="font-size: 14px; font-family: 宋体; color: windowtext;">直系亲属</span></p><p style="white-space: normal; text-indent: 24px;"><span style="color: windowtext; font-family: 宋体; font-size: 14px;">是指被保险人的配偶、父母、岳父母、子女或子女的配偶、兄弟或姐妹、（外）祖父母、（外）孙子女，被保险人父母的兄弟或姐妹，被保险人配偶的兄弟或姐妹。</span></p><p style="white-space: normal;"><span style="font-family: 宋体;">&nbsp;</span></p><p style="white-space: normal;"><br/></p><p><br/></p>', '境外旅行意外伤害保险');
INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35199d9410151a3f25ed200a5', '8a8195b35112ed70015112f3280d0002', '1', '<p><img src="http://image.fubon.com.cn/fmps-uat/upload/image/20151215/1450154536942007439.jpg" title="1450154536942007439.jpg" alt="Chrysanthemum.jpg"/></p>', 'xxxx');
INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35155f9cc01515626a41f0002', '8a8195b35112ed70015112f3280d0002', '2', '<p>1.保险公司认为客户购买行为是在认真阅读、理解并接受相关保险内容基础上进行的，购买前请务必确保了解产品相关的条款及约定。</p><p>2.年龄在18周岁以上、具有完全民事行为能力的被保险人本人，对被保险人有保险利益的其它人可作为投保人。</p><p>3.本保险产品被保险人年龄范围为1周岁至30周岁，每一被保险人仅限购买1份。</p><p>4.未满18周岁的未成年人的“意外身故、伤残保障”的保险金为10万元。</p><p>5.本保险产品承保旅行地为”台湾“。</p><p>6.投保人可于保险合同成立后，保险责任开始前书面申请撤销本合同，本公司进行审查后符合条件的将退还已缴保费 ，保险合同关系自本公司同意退费之日解除。除保险期间为一年期的保单，一旦保险责任开始，保险合同将不予解除,并不予退还保险费。</p><p>7.任何在下列期间发生的或由下列原因造成的保险事故，本公司不负任何赔偿责任：战争、军事行动、暴动或武装叛乱期间；侵略、外敌行为、敌对(不论是否宣战)、内战、叛乱、革命、起义、军事行动或篡权、受任何政府或国家权力机构的指挥对财产的没收或国有化或征用或毁坏或破坏的、暴乱骚乱等。</p><p><br/></p>', '须知');
INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35112ed70015112f54a5c0004', '8a8195b35112ed70015112f3280d0002', '3', '<p><img src="http://image.fubon.com.cn/fmps-uat/upload/image/20151210/1449726452827047011.jpg" title="1449726452827047011.jpg" alt="商品介绍.jpg"/></p>', '须知');
INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35112ed70015112f594060005', '8a8195b35112ed70015112f3280d0002', '4', '', '须知');
INSERT INTO `weixin_product_affiliated` (`id`, `productid`, `type`, `document`, `description`) VALUES ('8a8195b35112ed70015112f5d5b00006', '8a8195b35112ed70015112f3280d0002', '5', '<p><span style="font-size: 16px;">Q：赴台学生保险的保障范围有哪些？</span></p><p><br/></p><p><span style="font-size: 16px;">A：赴台学生保险涵括意外伤害风险、急性病风险、医疗救助、紧急救援、旅行不便、个人责任六大保障，为赴台研习学生提供全方位保障。</span></p><p><br/></p><p><span style="font-size: 16px;">Q：个人责任保障具体是指什么内容？</span></p><p><br/></p><p><span style="font-size: 16px;">A：个人责任保障是指被保险人在台湾期间因疏忽或过失行为造成第三方死亡、身体伤害或直接财产损失，对于依法应由被保险人承担的经济赔偿责任，第三方在保险期间内向被保险人提出索赔的，保险人依约定给付保险金。例如：打球时误伤同学、骑脚踏车误伤路人、不小心损坏学校财务或百货公司精品等。</span></p><p><br/></p><p><span style="font-size: 16px;">Q：学生在台出险，理赔需要回到大陆才能申请吗?</span></p><p><br/></p><p><span style="font-size: 16px;">A：首先，为便利于大陆学生在台湾出险报案，富邦财险于台湾地区特别架设24小时报案专线，不需 用国际漫游即可报案，免除两岸报案繁琐、快速又便捷！台湾手机（固话）报案请拨：02-66031581，大陆手机（固话）报案请拨：+886-2-66031581；</span></p><p><br/></p>', '须知');
